import React, { useState, useEffect } from 'react';
import { Text, View, TextInput } from 'react-native';
import Form from "../../assets/styles/formStyle"
import DropDownPicker from 'react-native-dropdown-picker';
import { Picker } from '@react-native-picker/picker';
import DateTimePicker from '@react-native-community/datetimepicker';
function StepThreeA(props) {
    const [display, setDisplay] = useState('none');
    const [sintomatico, setSintomatico] = useState('No');
    const [showFechaSintomas, setShowFechaSintomas] = useState(false)
    const [valueEstado, setValueEstado] = useState(null);
    const [itemsEstado, setItemsEstado] = useState([]);
    const [valueMunicipio, setValueMunicipio] = useState(null);
    const [itemsMunicipio, setItemsMunicipio] = useState([]);
    const [valueParroquia, setValueParroquia] = useState(null);
    const [itemsParroquia, setItemsParroquia] = useState([]);
    const [date, setDate] = useState(new Date());
    const [show, setShow] = useState(false);
    const [fechaPrint, setFechaPrint] = useState('');
    const DisplayData = Platform.OS === 'ios' ? 'spinner' : 'calendar';
    let controller;
    const onChange = (event, selectedDate) => {
        const currentDate = selectedDate || date;
        setShow(Platform.OS === 'ios');
        setDate(currentDate);
        var dd = String(selectedDate.getDate()).padStart(2, '0');
        var mm = String(selectedDate.getMonth() + 1).padStart(2, '0');
        var yyyy = selectedDate.getFullYear();
        setFechaPrint(dd + '/' + mm + '/' + yyyy)
        functionSetFechaSintoma(fechaPrint);
    };
    const showDatepicker = () => {
        setShow(true);
    };
    useEffect(() => {
        if (props.currentStep === 3) {
            setDisplay('flex')
        } else {
            setDisplay('none');
        }
    }, [props.currentStep])
    const {
        functionSetEstadoLugarAtencion,
        functionSetMunicipioLugarAtencion,
        functionSetParroquiaLugarAtencion,
        functionSetMedico,
        functionSetSintomatico,
        functionSetFechaSintoma,
        functionSetPoblacionRiesgo
    } = props;
    const sintomaticoSetValues = (itemValue) => {
        setSintomatico(itemValue); 
        functionSetSintomatico(itemValue); 
        setShowFechaSintomas(itemValue)
    }
    return (
        <View style={[Form.Form, { display: display }]}>
            <View style={Form.FormHead}>
                <Text style={Form.FormHeadText}>
                    ANTECEDENTES EPIDEMIOLÓGICOS
            </Text>
            </View>
            <View style={Form.FormSubHead}>
                <Text style={[Form.FormSubHeadText, { marginTop: 24 }]}>
                    LUGAR DE ATENCIÓN
                </Text>
            </View>
            <View style={[Form.InputContOne, { marginTop: 15, marginBottom: 15 }]}>
                <Text style={Form.InputLabel}>ESTADO</Text>
                <DropDownPicker
                    items={itemsEstado}
                    style={Form.DropDown}
                    containerStyle={Form.DropDownContainer}
                    controller={instance => controller = instance}
                    onChangeList={(items, callback) => {
                        new Promise((resolve, reject) => resolve(setItemsEstado(items)))
                            .then(() => callback())
                            .catch(() => { });
                    }}
                    searchable={true}
                    searchablePlaceholder="Search for an item"
                    searchablePlaceholderTextColor="gray"
                    defaultValue={valueEstado}
                    onChangeItem={item => setValueEstado(item.value)}
                />
            </View>
            <View style={[Form.InputContOne, { marginTop: 15, marginBottom: 15 }]}>
                <Text style={Form.InputLabel}>MUNICIPIO</Text>
                <DropDownPicker
                    items={itemsMunicipio}
                    style={Form.DropDown}
                    containerStyle={Form.DropDownContainer}
                    controller={instance => controller = instance}
                    onChangeList={(items, callback) => {
                        new Promise((resolve, reject) => resolve(setItemsMunicipio(items)))
                            .then(() => callback())
                            .catch(() => { });
                    }}
                    searchable={true}
                    searchablePlaceholder="Search for an item"
                    searchablePlaceholderTextColor="gray"
                    defaultValue={valueMunicipio}
                    onChangeItem={item => setValueMunicipio(item.value)}
                />
            </View>
            <View style={[Form.InputContOne, { marginTop: 15, marginBottom: 15 }]}>
                <Text style={Form.InputLabel}>PARROQUIA</Text>
                <DropDownPicker
                    items={itemsParroquia}
                    style={Form.DropDown}
                    containerStyle={Form.DropDownContainer}
                    controller={instance => controller = instance}
                    onChangeList={(items, callback) => {
                        new Promise((resolve, reject) => resolve(setItemsParroquia(items)))
                            .then(() => callback())
                            .catch(() => { });
                    }}
                    searchable={true}
                    searchablePlaceholder="Search for an item"
                    searchablePlaceholderTextColor="gray"
                    defaultValue={valueParroquia}
                    onChangeItem={item => setValueParroquia(item.value)}
                />
            </View>
            <View style={Form.FormSubHead}>
                <Text style={Form.FormSubHeadText}>
                    CONDICIÓN DE SOSPECHA
                </Text>
            </View>
            <View style={[Form.InputContOne, { marginTop: 15, marginBottom: 15 }]}>
                <Text style={Form.InputLabel}>MÉDICO</Text>
                <TextInput
                    style={Form.Input}
                    onChangeText={text => functionSetMedico(text)}
                />
            </View>
            <View style={[Form.InputContOne, { marginTop: 15, marginBottom: 15 }]}>
                <Text style={Form.InputLabel}>¿SINTOMATICO?</Text>
                <Picker
                        selectedValue={sintomatico}
                        style={{ height: 50, width: "100%" }}
                        onValueChange={(itemValue, itemIndex) => sintomaticoSetValues(itemValue)}
                    >
                        <Picker.Item label="No" value={false} />
                        <Picker.Item label="Si" value={true} />
                    </Picker>
            </View>
            {showFechaSintomas && (
                <View style={[Form.InputContOne, { marginTop: 15, marginBottom: 15 }]}>
                <Text style={Form.InputLabel}>FECHA INICIO DE SINTOMAS</Text>
                <TextInput
                    onFocus={showDatepicker}
                    defaultValue={fechaPrint}
                    style={Form.Input}
                />
            </View>
            )}
            <View style={Form.FormSubHead}>
                <Text style={Form.FormSubHeadText}>
                    ANTECEDENTES
                </Text>
            </View>
            <View style={[Form.InputContOne, { marginTop: 15, marginBottom: 15 }]}>
                <Text style={Form.InputLabel}>POBLACIÓN DE RIESGO</Text>
                <TextInput
                    style={Form.Input}
                    onChangeText={text => functionSetPoblacionRiesgo(text)}
                />
            </View>
            {show && (
                <DateTimePicker
                    testID="dateTimePicker"
                    value={date}
                    mode={'data'}
                    display={DisplayData}
                    onChange={onChange}
                />
            )}
        </View>
    );
}

export default StepThreeA;
