import React, { useContext, useState } from "react";
import axios from 'axios';
import qs from 'qs';
import { Image, View, TextInput, Text, TouchableHighlight, KeyboardAvoidingView, Platform, Alert } from "react-native";
import login from "../assets/styles/loginStyles";
import { database } from "../db/database";
import { GlobalContext } from '../context/MyContext'
export default function LoginSceen({ navigation }) {
  const [dataUser, setDataUser] = useState([]);
  const [isRegisterLocal, setregisterLocal] = useState(false)
  const [username, setUsername] = useState('');
  const [passsword, setPassword] = useState();
  const gC = useContext(GlobalContext);
  const funcSetregisterLocal = () => {
    setregisterLocal();
  }
  const funcSetDataUser = (val) => {
    setDataUser(val)
  }
  const formData = new FormData();
  // TODO AGG PANTALLA DE CARGA
  const goToHome = async () => {
    /**
     * ? consultamos si existe la table de usuarios si es asi consultamos alli primero
     */
    database.setupDatabaseUserAsync()
    database.getUSer(username, passsword, funcSetDataUser);
    //console.log(dataUser)
     if (dataUser === null || dataUser.length === 0) {
      formData.append("user", username);
      formData.append("password", passsword);
      await axios.post(`http://sistemas.mpps.gob.ve/index.php/api/login`,
        formData,
        {
          header: {
            'Content-Type': 'application/x-www-form-urlencoded'
          }
        }).then(r => {
          if (r.data !== null) {
            
            database.insertUser(r.data, username, passsword, funcSetregisterLocal);
            if (isRegisterLocal) {
              gC.setIdUser(r.data);
              database.setupDatabaseAsync();
              database.getFicha(gC.setDataGlobal);
              navigation.navigate('PrincipalPages');
            }else {
              alert('no se pudieron guardar los datos del usuario localmente');
              gC.setIdUser(r.data);
              database.setupDatabaseAsync();
              database.getFicha(gC.setDataGlobal);
              navigation.navigate('PrincipalPages');
            } 
          }
        })
        .catch(e => {
          alert('Sus datos son incorrecto');
        })
    }else {
      console.log(dataUser)
    } 
  }
  return (
    <KeyboardAvoidingView
      behavior={Platform.OS == "ios" ? "padding" : "height"}
      style={login.container}
    >
      <View style={login.bgGroundDark}>
        <View style={login.logoLogin}>
          <Image source={require("../assets/images/mpps-logo.png")} />
        </View>
        <View style={login.inputContainerDark}>
          <TextInput
            style={login.inputDark}
            autoCompleteType="username"
            autoFocus
            placeholder="Usuario"
            placeholderTextColor="white"
            onChangeText={text => setUsername(text)}
          />
          <TextInput
            style={login.inputDark}
            autoCompleteType="password"
            placeholder="ContraseÃ±a"
            placeholderTextColor="white"
            textContentType="password"
            secureTextEntry
            onChangeText={text => setPassword(text)}
          />
        </View>
        <View style={login.contentButonLogin}>
          <TouchableHighlight onPress={() => goToHome()} underlayColor='transparent'>
            <View style={login.buttLogin}>
              <Text style={login.textButoon}>Iniciar</Text>
            </View>
          </TouchableHighlight>
        </View>
        {/**
      <View style={login.singInFingerCont}>
        <TouchableHighlight>
          <View style={login.buttLoginFinger}>
            <View style={login.textFingerCont}>
              <Text style={login.textButoonFinger}>
                Ingresar con huella dactilar
              </Text>
            </View>
              * ?paradespues TODO <View style={{ marginLeft: 44 }}>
              <Image
                style={login.huellaSingin}
                source={require("../assets/images/huella-dactilar.png")}
              />
          </View>
        </TouchableHighlight>
      </View>
            </View> */}
        <View style={login.suisContText}>
          <Text style={login.suisText}>SUIS</Text>
        </View>
      </View>
    </KeyboardAvoidingView>
  );
}
